namespace Oft\Test\Mvc\Context;

use Oft\Mvc\Context\ContextAbstract;
use PHPUnit_Framework_TestCase;

class TestContext extends ContextAbstract
{
    protected $contexts = array('p' => null, 'p2' => null);
    
    public function testSet($name, $context)
    {
        return $this->setContext($name, $context);
    }
}

/**
 * Generated by PHPUnit_SkeletonGenerator on 2014-11-25 at 10:51:03.
 */
class ContextAbstractTest extends PHPUnit_Framework_TestCase
{

    /**
     * @var ContextAbstract
     */
    protected $context;

    public function testMagicGetFailWhenConstructWithoutParameters()
    {
        $this->setExpectedException('RuntimeException');
        
        $context = new TestContext();
        $context->something;
    }

    public function testContructWithParameters()
    {
        $context =new TestContext(array('p' => 'v', 'p2' => null));

        $context->testSet('p2', 'v2');

        $this->assertSame('v', $context->p);
        $this->assertSame('v2', $context->p2);
    }
}
