namespace Oft\Test\Mvc;

/**
 * Generated by PHPUnit_SkeletonGenerator on 2014-07-10 at 12:10:32.
 */
class MiddlewareAbstractTest extends \PHPUnit_Framework_TestCase
{

    /**
     * @var \Oft\Mvc\MiddlewareAbstract
     */
    protected $middleware;

    protected function setUp()
    {
        $this->middleware = \Mockery::mock('Oft\Mvc\MiddlewareAbstract')->makePartial();
    }

    protected function tearDown()
    {
        $this->middleware = null;
    }

    public function testSetNextMiddleware()
    {
        $nextMiddleware = \Mockery::mock('Oft\Mvc\MiddlewareAbstract');
        
        $this->middleware->setNextMiddleware($nextMiddleware);
        
        $this->assertSame($nextMiddleware, $this->middleware->getNextMiddleware());
    }

}
